import React, { useState } from "react";
import { Wheel } from "react-custom-roulette";
import Colors from "../colors/colors";
import { Typography, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
const data = [
  { option: "32" },
  { option: "15" },
  { option: "19" },
  { option: "4" },
  { option: "21" },
  { option: "2" },
  { option: "25" },
  { option: "17" },
  { option: "34" },
  { option: "6" },
  { option: "27" },
  { option: "13" },
  { option: "36" },
  { option: "11" },
  { option: "30" },
  { option: "8" },
  { option: "23" },
  { option: "10" },
  { option: "5" },
  { option: "24" },
  { option: "16" },
  { option: "33" },
  { option: "1" },
  { option: "20" },
  { option: "14" },
  { option: "31" },
  { option: "9" },
  { option: "22" },
  { option: "18" },
  { option: "29" },
  { option: "7" },
  { option: "28" },
  { option: "12" },
  { option: "35" },
  { option: "3" },
  { option: "26" },
  { option: "0", style: { backgroundColor: "#283618", textColor: "#FFFCF2" } },
];

const backgroundColors = ["#d90429", "#252422"];
const textColors = ["#FFFCF2"];
const outerBorderColor = "#252422";
const outerBorderWidth = 10;
const innerBorderColor = "#252422";
const innerBorderWidth = 20;
const innerRadius = 60;
const radiusLineColor = "#252422";
const radiusLineWidth = 3;
const fontSize = 13;
const textDistance = 85;

const Roulette = () => {
  const classes = useStyles();

  const [mustSpin, setMustSpin] = useState(false);
  const [prizeNumber, setPrizeNumber] = useState(0);
  const [perpendicularText] = useState(true);
  const [winnerNum, setWinNum] = useState(0);
  return (
    <div className={classes.main}>
      <Wheel
        mustStartSpinning={mustSpin}
        prizeNumber={prizeNumber}
        data={data}
        backgroundColors={backgroundColors}
        textColors={textColors}
        fontSize={fontSize}
        outerBorderColor={outerBorderColor}
        outerBorderWidth={outerBorderWidth}
        innerRadius={innerRadius}
        innerBorderColor={innerBorderColor}
        innerBorderWidth={innerBorderWidth}
        radiusLineColor={radiusLineColor}
        radiusLineWidth={radiusLineWidth}
        perpendicularText={perpendicularText}
        textDistance={textDistance}
        onStopSpinning={() => {
          setMustSpin(false);
        }}
      />
        <div className={classes.divWinner}>
        {mustSpin === false ? (
          <Typography className={classes.winner}>{winnerNum}</Typography>
        ) : (
          <Typography className={classes.winner}> </Typography>
        )}
      </div>
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  main: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  }
}));

export default Roulette;
