import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Typography, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Roulette from "../images/roulette.svg";
import Colors from "../colors/colors";
import ButtonMetamask from "./ButtonMetaMask"; 
const Landing = () => {
  const classes = useStyles();
  const [connected, setConnected] = useState(false);
  return (
    <div className={classes.container}>
      <div className={classes.divTitle}>
        <Typography className={classes.title}>
          Hi, we are ethereum roulette
        </Typography>
        <img className={classes.imageTitle} src={Roulette} alt="Roulette" />
      </div>
      {!connected ? (
        <div>
           <ButtonMetamask connected={setConnected} /> 
        </div>
      ) : (
        <div>
          <Link to={Roulette} className={classes.link}>
            <Button>
              <Typography>Go to the Roulette</Typography>
            </Button>
          </Link>
        </div>
      )}
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  container: {
    width: "100%",
    height: "100%",
    minHeight: "100vh",
    display: "flex",
    alignItems: "flex-start",
    justifyContent: "center",
    backgroundColor: Colors.grey,
    flexDirection: "column",
  },
  divTitle: {
    display: "flex",
    flexDirection: "column",
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontFamily: "Oswald",
    fontSize: "8vh",
    color: Colors.black,
    marginTop: "2%",
    marginBottom: "0%",
    textShadow: `2px 2px 0 ${Colors.orange}, 2px -2px 0  ${Colors.orange}, -2px 2px 0  ${Colors.orange}, -2px -2px 0  ${Colors.orange}, 2px 0px 0  ${Colors.orange}, 0px 2px 0  ${Colors.orange}, -2px 0px 0  ${Colors.orange}, 0px -2px 0  ${Colors.orange}`,
  },
  imageTitle: {
    marginTop: "5%",
    width: "10rem",
    height: "10rem",
  },
}));
export default Landing;
