import React, { useEffect, useState } from "react";
import Roulette from "./Roulette";
import { Typography, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Zoom } from "react-awesome-reveal";
import Pointer from "../images/pointer.png";
import useAccount from "../hooks/accounts";
import Colors from "../colors/colors";
import BackImage from "../images/back.png";

const Game = () => {
  const classes = useStyles();

  const { account, balance } = useAccount();

  const [acitveWheel, setActiveWhell] = useState(false);

  const [currentUser, setCurrentUser] = useState("");

  const [currentBalance, setCurrentBalance] = useState(0);

  useEffect(() => {
    setCurrentUser(account);
    setCurrentBalance(balance.slice(0, 8));
  }, [account, balance]);

  return (
    <div className={classes.container}>
      <Zoom className={classes.zoom}>
        <div className={classes.divTitle}>
          <Typography className={classes.title}>Give us your money</Typography>
        </div>
      </Zoom>
      <Zoom className={classes.zoom}>
        <div className={classes.divBoard}>
          <div className={classes.divUp}>
            <div className={classes.divWheel}>
              <Roulette />
            </div>
            <div className={classes.divInfo}>
              <Typography className={classes.info}>User: {currentUser}</Typography>
              <Typography className={classes.info}>Balance: {currentBalance} ethers</Typography>

            </div>
          </div>
        </div>
      </Zoom>
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  zoom: {
    display: "flex",
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
  },
  container: {
    width: "100%",
    height: "fit-content",
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-start",
    backgroundColor: Colors.grey,
    flexDirection: "column",
    backgroundImage: `url(${BackImage})`,
  },
  divTitle: {
    display: "flex",
    flexDirection: "column",
    width: "100%",
    alignItems: "center",
    justifyContent: "flex-start",
  },
  title: {
    fontFamily: ["Oswald", "cursive"].join(","),
    fontSize: "8vh",
    color: Colors.white,
    fontWeight: "bold",
    marginTop: "2%",
    marginBottom: "0%",
    textShadow: `2px 2px 0 ${Colors.black}, 2px -2px 0  ${Colors.black}, -2px 2px 0  ${Colors.black}, -2px -2px 0  ${Colors.black}, 2px 0px 0  ${Colors.black}, 0px 2px 0  ${Colors.black}, -2px 0px 0  ${Colors.black}, 0px -2px 0  ${Colors.black}`,
  },
  divBoard: {
    display: "flex",
    width: "90%",
    marginTop: "2%",
    flexDirection: "column",
    backgroundColor: Colors.white,
    alignItems: "flex-start",
    justifyContent: "flex-start",
    borderRadius: "10px 10px 10px 10px",
    border: `.5rem solid ${Colors.black}`,
  },
  divUp: {
    width: "100%",
    display: "flex",
    flexDirection: "row",
  },
  divWheel: {
    width: "40%",
    display: "flex",
    alignItems: "flex-start",
    justifyContent: "flex-start",
  },
  divInfo:{
    display: "flex",
    width: "50%",
    alignItems: "flex-start",
    justifyContent: "flex-end",
    flexDirection: "column"
  }
}));
export default Game;
