import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Typography, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Roulette from "../images/roulette.svg";
import Colors from "../colors/colors";
import ButtonMetamask from "./ButtonMetaMask";
import { Zoom } from "react-awesome-reveal";

const Landing = () => {
  const classes = useStyles();
  const [connected, setConnected] = useState(false);
  return (
    <div className={classes.container}>
      <Zoom>
        <div className={classes.divTitle}>
          <Typography className={classes.title}>
            Hi, we are ethereum roulette
          </Typography>
          <img className={classes.imageTitle} src={Roulette} alt="Roulette" />
        </div>
      </Zoom>

      <div className={classes.divTitle}>
        <Typography className={classes.subTitle}>
          Please before connect your wallet
        </Typography>
      </div>
      <div className={classes.divButtons}>
        {!connected ? (
          <div>
            <ButtonMetamask connected={setConnected} />
          </div>
        ) : (
          <div>
            <Link to={Roulette} className={classes.link}>
              <Button>
                <Typography>Go to the Roulette</Typography>
              </Button>
            </Link>
          </div>
        )}
      </div>
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  container: {
    width: "100%",
    height: "100%",
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-start",
    backgroundColor: Colors.grey,
    flexDirection: "column",
  },
  divTitle: {
    display: "flex",
    flexDirection: "column",
    width: "100%",
    alignItems: "center",
    justifyContent: "flex-start",
  },
  title: {
    fontFamily: ["Oswald", "cursive"].join(","),
    fontSize: "8vh",
    color: Colors.black,
    fontWeight: "bold",
    marginTop: "2%",
    marginBottom: "0%",
    textShadow: `2px 2px 0 ${Colors.orange}, 2px -2px 0  ${Colors.orange}, -2px 2px 0  ${Colors.orange}, -2px -2px 0  ${Colors.orange}, 2px 0px 0  ${Colors.orange}, 0px 2px 0  ${Colors.orange}, -2px 0px 0  ${Colors.orange}, 0px -2px 0  ${Colors.orange}`,
  },
  subTitle: {
    fontFamily: ["Poppins", "cursive"].join(","),
    fontSize: "3vh",
    color: Colors.black,
    marginTop: "2%",
    fontWeight: "bold",
    marginBottom: "0%",
  },
  imageTitle: {
    marginTop: "5%",
    width: "10rem",
    height: "10rem",
  },
  divButtons: {
    display: "flex",
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
    marginTop: "5%",
  },
}));
export default Landing;
